<?php    session_start();    $conn=mysqli_connect("localhost","root","","ehsas_db");    $eno=$_POST['eno'];    $name=$_POST['name'];    $pass=$_POST['pass'];    $dob=$_POST['dob'];    $num=$_POST['num'];    if (!empty($eno) && !empty($name) && !empty($pass) && !empty($dob) && !empty($num)) {        $duplicate=mysqli_query($conn,"select * from student_list where enrollment_no='$eno'");        if (mysqli_num_rows($duplicate)>0){            $sql="UPDATE student_list SET student_name='$name',student_password='$pass',date_of_birth='$dob',mobile_no='$num' WHERE enrollment_no='$eno'";            $result=mysqli_query($conn,$sql);            if (isset($result)) {                $_SESSION['status']="true";                header("Location:edit-student.php");            }            else{                $_SESSION['status']="false";                header("Location:edit-student.php");            }                    }        else{            $_SESSION['status']="dupe";            header("Location:edit-student.php");        }    }    else{        $_SESSION['status']="false";        header("Location:edit-student.php");    }?>